INSERT INTO `Template` (`template_id`, `template_name`, `template_type`, `template_text`) VALUES
(1, 'forgot_passwd', 'systm', '<template deflang="en">\r\n<TO email="$user_firstname $user_lastname &lt;$user_email&gt;"/>\r\n\r\n<subject lang="en" value="Your new password"/>\r\n<subject lang="fr" value="Votre nouveau mot de passe"/>\r\n<subject lang="it" value="La Sua nuova parola d''accesso"/>\r\n\r\n<text lang="en">\r\nDear $user_firstname $user_lastname!\r\n\r\nThere is your new password: $new_password\r\n\r\nHave a nice day\r\n\r\n</text>\r\n\r\n<html lang="en">\r\n<p>Dear $user_firstname $user_lastname!</p>\r\n\r\n<p>There is your new password: <b>$new_password</b></p>\r\n\r\n<p>Have a nice day!</p>\r\n \r\n\r\n</html>\r\n\r\n<text lang="fr">\r\nCher/ChÃ¨re $user_firstname $user_lastname!\r\n\r\nVotre nouveau mot de passe est: $new_password\r\n\r\nMeilleures salutations\r\n\r\n</text>\r\n\r\n<html lang="fr">\r\n<p>Cher/ChÃ¨re $user_firstname $user_lastname!</p>\r\n\r\n<p>Votre nouveau mot de passe est: <b>$new_password</b></p>\r\n\r\n<p>Meilleures salutations</p>\r\n \r\n</html>\r\n\r\n\r\n<text lang="it">\r\nGentile/Gentilissima $user_firstname $user_lastname!\r\n\r\nLa Sua nuova parola d''accesso Ã¨: $new_password\r\nDistinti saluti\r\n\r\n</text>\r\n\r\n<html lang="it">\r\n<p>Gentile/Gentilissima $user_firstname $user_lastname!</p>\r\n\r\n<p>La Sua nuova parola d''accesso Ã¨: <b>$new_password</b></p>\r\n\r\n<p>Distinti saluti</p>\r\n \r\n\r\n</html>\r\n\r\n</template>\r\n'),
(2, 'Signup_email' , 'systm', '<template deflang="en">\r\n<TO email="$user_firstname $user_lastname &lt;$user_email&gt;"/>\r\n\r\n<subject lang="en" value="Confimation of registration"/>\r\n<subject lang="fr" value="Votre nouveau mot de passe"/>\r\n<subject lang="it" value="La Sua nuova parola d''accesso"/>\r\n\r\n<text lang="en">\r\nDear $user_firstname $user_lastname!\r\n\r\nPlease click on the link below:\r\n\r\nPlease copy the following link into your browser:\r\n$link\r\n\r\nThank you\r\n\r\n</text>\r\n\r\n<html lang="en">\r\n<p>Dear $user_firstname $user_lastname!</p>\r\n\r\n<p>Please click on the link below:</p>\r\n\r\n<a href="$link">Click Here</a>\r\n\r\n<p>Thank You</p>\r\n \r\n</html>\r\n\r\n<text lang="fr">\r\nCher/ChÃ¨re $user_firstname $user_lastname!\r\n\r\nVotre nouveau mot de passe est: $new_password\r\n\r\nMeilleures salutations\r\n\r\n</text>\r\n\r\n<html lang="fr">\r\n<p>Cher/ChÃ¨re $user_firstname $user_lastname!</p>\r\n\r\n<p>Votre nouveau mot de passe est: <b>$new_password</b></p>\r\n\r\n<p>Meilleures salutations</p>\r\n \r\n</html>\r\n\r\n\r\n<text lang="it">\r\nGentile/Gentilissima $user_firstname $user_lastname!\r\n\r\nLa Sua nuova parola d''accesso Ã¨: $new_password\r\nDistinti saluti\r\n\r\n</text>\r\n\r\n<html lang="it">\r\n<p>Gentile/Gentilissima $user_firstname $user_lastname!</p>\r\n\r\n<p>La Sua nuova parola d''accesso Ã¨: <b>$new_password</b></p>\r\n\r\n<p>Distinti saluti</p>\r\n \r\n\r\n</html>\r\n\r\n</template>\r\n'),
(3, 'email_res'    , 'systm', '<?xml version="1.0" encoding="UTF-8" ?>\r\n<template deflang="en">\r\n<FROM email="Demo Theater &lt;demo@fusionticket.co.uk&gt;"/>\r\n<TO email="$user_firstname $user_lastname &lt;$user_email&gt;"/>\r\n<CC email="log@demo.mail.com"/>\r\n<BCC email="log@demo.mail.com"/>\r\n<HEADER name="2" value="1"/>\r\n<subject lang="en" value="Confirmation of your tickets, order no. $order_id"/>\r\n<subject lang="de" value="BestÃƒÂ¤tigung Ihre Bestellung no. $order_id"/>\r\n<subject lang="fr" value="Confirmation de Votre commande nr. $order_id"/>\r\n<subject lang="it" value="Conferma del Suo ordine nr. $order_id"/>\r\n\r\n<text lang="en">\r\nTEST SYSTEM - PLEASE EDIT IN TEMPLATES!\r\n\r\nDear $user_firstname $user_lastname!\r\n\r\nThis E-Mail confirms your ticket order from Demo \r\nYour reservation number is: $order_id\r\n\r\nYour tickets are only reserved for a limited time. To purchase the tickets please go to: $order_link$order_id\r\nThank you very much.\r\n\r\n$order_date\r\n\r\n</text>\r\n\r\n<html lang="en">\r\n<b>TEST SYSTEM - PLEASE EDIT IN TEMPLATES!</b>\r\n\r\n<p>Dear $user_firstname $user_lastname!</p>\r\n\r\n<p>This E-Mail confirms your ticket order from Demo </p>\r\n<p>Your purchase number is: <b>$order_id</b></p>\r\n\r\n<p>Your tickets are only reserved for a limited time. To purchase the tickets please go <a href="$order_link$order_id"> here</a>\r\n</p>\r\n\r\n<p>\r\nThank you very much.\r\n</p><p>\r\n$order_date\r\n\r\n</p>\r\n</html>\r\n</template>' );
