ail'',''user_firstname'',''user_lastname'',''link'',''user_firstname'',''user_lastname'',''link'',''user_firstname'',''user_lastname'',''new_password'',''user_firstname'',''user_lastname'',''new_password'',''user_firstname'',''user_lastname'',''new_password'',''user_firstname'',''user_lastname'',''new_password'');}\n  \n}\n', 3);
INSERT INTO `Template` (`template_id`, `template_name`, `template_type`, `template_text`, `template_ts`, `template_status`, `template_code`, `template_organizer_id`) VALUES
(21, 'email_res', 'email', '<?xml version="1.0" encoding="UTF-8" ?>\r\n<template deflang="en">\r\n<FROM email="Demo Theater &lt;demo@fusionticket.co.uk&gt;"/>\r\n<TO email="$user_firstname $user_lastname &lt;$user_email&gt;"/>\r\n<CC email="log@demo.mail.com"/>\r\n<BCC email="log@demo.mail.com"/>\r\n<HEADER name="2" value="1"/>\r\n<subject lang="en" value="Confirmation of your tickets, order no. $order_id"/>\r\n<subject lang="de" value="BestÃƒÂ¤tigung Ihre Bestellung no. $order_id"/>\r\n<subject lang="fr" value="Confirmation de Votre commande nr. $order_id"/>\r\n<subject lang="it" value="Conferma del Suo ordine nr. $order_id"/>\r\n\r\n<text lang="en">\r\nTEST SYSTEM - PLEASE EDIT IN TEMPLATES!\r\n\r\nDear $user_firstname $user_lastname!\r\n\r\nThis E-Mail confirms your ticket order from Demo \r\nYour reservation number is: $order_id\r\n\r\nYour tickets are only reserved for a limited time. To purchase the tickets please go to: $order_link$order_id\r\nThank you very much.\r\n\r\n$order_date\r\n\r\n</text>\r\n\r\n<html lang="en">\r\n<b>TEST SYSTEM - PLEASE EDIT IN TEMPLATES!</b>\r\n\r\n<p>Dear $user_firstname $user_lastname!</p>\r\n\r\n<p>This E-Mail confirms your ticket order from Demo </p>\r\n<p>Your purchase number is: <b>$order_id</b></p>\r\n\r\n<p>Your tickets are only reserved for a limited time. To purchase the tickets please go <a href="$order_link$order_id"> here</a>\r\n</p>\r\n\r\n<p>\r\nThank you very much.\r\n</p><p>\r\n$order_date\r\n\r\n</p>\r\n</html>\r\n</template>', '2008-05-22 13:43:48', 'comp', '\n/*this is a generated code. do not edit!\n\nproduced Thursday 22nd 2008f May 2008 01:43:48 PM \n\n*/\n\nclass TT_email_res {\n  var $object_id;\n  var $engine;\n  \n  function TT_email_res(){}\n  function build(&$mail,&$data,$lang="en"){\n    if($lang=="en"){\n      $mail->setTextCharset("UTF-8");\n      $mail->setHtmlCharset("UTF-8");\n      $mail->setHeadCharset("UTF-8");\n      $mail->setSubject("Confirmation of your tickets, order no. ".$data["order_id"]."");\n      $mail->setHtml("\n<b >TEST SYSTEM - PLEASE EDIT IN TEMPLATES!</b>\n\n<p >Dear ".$data["user_firstname"]." ".$data["user_lastname"]."!</p>\n\n<p >This E-Mail confirms your ticket order from Demo </p>\n<p >Your purchase number is: <b >".$data["order_id"]."</b></p>\n\n<p >Your tickets are only reserved for a limited time. To purchase the tickets please go <a HREF=\\"".$data["order_link"]."".$data["order_id"]."\\" > here</a>\n</p>\n\n<p >\nThank you very much.\n</p><p >\n".$data["order_date"]."\n\n</p>\n","\nTEST SYSTEM - PLEASE EDIT IN TEMPLATES!\n\nDear ".$data["user_firstname"]." ".$data["user_lastname"]."!\n\nThis E-Mail confirms your ticket order from Demo \nYour reservation number is: ".$data["order_id"]."\n\nYour tickets are only reserved for a limited time. To purchase the tickets please go to: ".$data["order_link"]."".$data["order_id"]."\nThank you very much.\n\n".$data["order_date"]."\n\n");\n    }\n    else if($lang=="de"){\n      $mail->setTextCharset("UTF-8");\n      $mail->setHtmlCharset("UTF-8");\n      $mail->setHeadCharset("UTF-8");\n      $mail->setSubject("BestÃƒÂ¤tigung Ihre Bestellung no. ".$data["order_id"]."");\n    }\n    else if($lang=="fr"){\n      $mail->setTextCharset("UTF-8");\n      $mail->setHtmlCharset("UTF-8");\n      $mail->setHeadCharset("UTF-8");\n      $mail->setSubject("Confirmation de Votre commande nr. ".$data["order_id"]."");\n    }\n    else if($lang=="it"){\n      $mail->setTextCharset("UTF-8");\n      $mail->setHtmlCharset("UTF-8");\n      $mail->setHeadCharset("UTF-8");\n      $mail->setSubject("Conferma del Suo ordine nr. ".$data["order_id"]."");\n    }\n      $mail->setFrom("Demo Theater <demo@fusionticket.co.uk>");\n      $mail->setCc("log@demo.mail.com");\n      $mail->setBcc("log@demo.mail.com");\n      $mail->setTextCharset("UTF-8");\n      $mail->setHtmlCharset("UTF-8");\n      $mail->setHeadCharset("UTF-8");\n      $mail->setHeader("2","1");\n      $this->to[]="".$data["user_firstname"]." ".$data["user_lastname"]." <".$data["user_email"].">";\n  }\n\n\nfunction get_used_vars(){ return array(''user_firstname'',''user_lastname'',''user_email'',''order_id'',''order_id'',''order_id'',''order_id'',''user_firstname'',''user_lastname'',''order_id'',''order_link'',''order_id'',''order_date'',''user_firstname'',''user_lastname'',''order_id'',''order_link'',''order_id'',''order_date'');}\n  \n}\n', 3);
